mapper.select:
select DISTINCT a.id, a.title, a.description, a.body, a.created_at, a.updated_at, u.id as uid, u.username as author_username, u.bio as author_bio, u.image as author_image, (select count(1) from article_favorites af where af.article_id = a.id) as favorites_count from articles as a left join article_tags t on a.id = t.article_id left join tags on t.tag_id = tags.id left join users as u on u.id = a.user_id left join article_favorites af on a.id = af.article_id where 1 = 1
and u.id = #{author}
and t.tag_id = #{tag}
and af.user_id = #{favorited}
order by a.updated_at desc limit #{offset},#{limit}
